  return (
    <div>
      <h1>
        <h2>The</h2>Siren
      </h1>{" "}
      <button>Get Started</button>
    </div>
  );
};

export const ShowUser = (props) => {
  return (
      <div className="shousr">
        <img
          src={
            "https://www.pikpng.com/pngl/m/154-1540525_male-user-filled-icon-my-profile-icon-png.png"
          }
        />
        <span>
          <h5>User Name !!!</h5>
          <small>{props.data1} - 10 mins read</small>
        </span>
      </div>
  );
};



export const ShowFooter = () => {
  const [dataOne] = useContext(contextData);
  const {id}=useParams();
  let d1;
  dataOne.forEach(element => {
    if(element.id==id)
    d1= element;
  });

  return (<><h5 className="hdsf">More From Siren</h5><hr style={{width:"80%",marginLeft:"9.4rem"}}/>

    <div className="showftr">
      {dataOne.filter((x)=>x.id>id-2&&x.id<id+2).map((x)=>{
        <div>
        <h6>Realted reads</h6>
        <img src={x.image}/>
        </div>
      })}
    </div></>
  )
}
